type DataResponse =
  | {
      message?: string
      code?: number
    }
  | any

class BuildResponse {
  private static baseResponse(dataResponse: DataResponse) {
    const {
      message = '[buildResponse.base]',
      code = 200,
      ...rest
    } = dataResponse
    return {
      code,
      message,
      ...rest,
    }
  }

  /**
   * Response Success
